INSERT INTO `issues` (`id`, `issue`, `solution`, `created_at`, `updated_at`) VALUES
(1, 'printer_setup', '- Plug in the printer and turn it on.<br>\r\n- Install ink cartridges and load paper.<br>\r\n- Use the printer screen or app to connect to Wi-Fi.<br>\r\n- Download and install the printer software.<br>\r\n- Follow the setup wizard to complete configuration.<br>', NULL, NULL),
(2, 'printer_offline', '- Make sure the printer is powered on and connected to the same network.<br>\r\n- Check printer status in your computer’s settings.<br>\r\n- Clear pending print jobs from the queue.<br>\r\n- Reconnect the printer using the software or app.<br>', NULL, NULL),
(3, 'wireless_printer', '- Place the printer within Wi-Fi signal range.<br>\r\n- Open the wireless setup on the printer display.<br>\r\n- Connect to your Wi-Fi network using the correct password.<br>\r\n- Verify connection by printing a network report.<br>', NULL, NULL),
(4, 'scanner_issues', '- Confirm the scanner is properly connected via USB or Wi-Fi.<br>\r\n- Check that the correct scanner is selected in the scanning software.<br>\r\n- Update or reinstall scanning software or drivers.<br>', NULL, NULL),
(5, 'ink_not_printing', '- Verify ink levels through the printer display or app.<br>\r\n- Run the printhead cleaning utility from the settings.<br>\r\n- Reset the cartridges firmly in their slots.<br>\r\n- Perform a print quality diagnostic page.<br>', NULL, NULL),
(6, 'cartridge_issues', '- Ensure you are using genuine, compatible cartridges.<br>\r\n- Remove any protective tape or packaging.<br>\r\n- Wipe cartridge contacts with a lint-free cloth.<br>\r\n- Reset the cartridges into their proper positions.<br>', NULL, NULL),
(7, 'job_stuck_queue', '- Open the print queue via control panel or settings.<br>\r\n- Cancel all pending jobs.<br>\r\n- Use the printer software to clear any errors.<br>\r\n- End the print spooler service and restart it from services.msc .<br>', NULL, NULL),
(8, 'paper_jam_issue', '- Open all accessible panels to inspect the paper path.<br>\r\n- Carefully pull out any jammed or torn paper.<br>\r\n- Check the input and output trays for obstructions.<br>\r\n- Inspect rollers and gently remove any debris.<br>', NULL, NULL),
(9, 'instant_ink_issues', '- Check your HP Instant Ink account for subscription or payment issues.<br>\r\n- Make sure the Instant Ink cartridges are installed correctly.<br>\r\n- Verify your printer is connected to the internet.<br>', NULL, NULL),
(10, 'carriage_jammed', '- Power off the printer and unplug it for safety.<br>\r\n- Open the access door to view the carriage.<br>\r\n- Remove any visible obstructions or bits of paper.<br>\r\n- Gently slide the carriage left and right to check movement.<br>', NULL, NULL),
(11, 'fax_issues', '- Ensure the phone line is connected to the correct fax port.<br>\r\n- Confirm that your fax settings (tone/pulse, resolution) are correct.<br>\r\n- Run a line test or use a test fax number if provided.<br>\r\n- Remove any old or stuck faxes from memory.<br>', NULL, NULL),
(12, 'download_driver', '- Open the printer software or settings on your computer.<br>\r\n- Enter your printer model in the search bar.<br>\r\n- Select your operating system.<br>\r\n- Download the latest driver or full software package.<br>', NULL, NULL);
